import { Inject, LOCALE_ID } from '@angular/core';
import { environment } from 'src/environments/environment';

export class Tools {
	//CLOUDINARY URLs
	static DESKTOP_CLOUDINARY_BASE_URL: string =
		'https://res.cloudinary.com/whydonate/image/upload/w_184,h_42,dpr_auto,f_auto,q_auto/whydonate-staging/platform/';
	static MOBILE_CLOUDINARY_BASE_URL =
		'https://res.cloudinary.com/whydonate/image/upload/w_92,h_21,dpr_auto,f_auto,q_auto/whydonate-staging/platform/';
	static CLOUDINARY_BASE_URL =
		'https://res.cloudinary.com/whydonate/image/upload/f_auto,q_auto/whydonate-staging/platform/';
	static WHYDONATE_LOGO =
		'https://res.cloudinary.com/whydonate/image/upload/dpr_auto,q_auto/whydonate-production/platform/visuals/new_design_logo.svg';

	static FUNDRAISER_DEFAULT_BG_BASE64 = `data:image/jpeg;base64,`;

	static getDesktopCloudinaryBaseUrl() {
		return this.DESKTOP_CLOUDINARY_BASE_URL;
	}

	static getMobileCloudinaryBaseUrl() {
		return this.MOBILE_CLOUDINARY_BASE_URL;
	}

	static getCloudinaryBaseUrl() {
		return this.CLOUDINARY_BASE_URL;
	}

	static getWhydonateLogo() {
		return this.WHYDONATE_LOGO;
	}

	static getBase64_FundraiserDefaultBg() {
		return this.FUNDRAISER_DEFAULT_BG_BASE64;
	}

	static setUserlanguage(language: string) {
		localStorage.setItem('userLang', language);
	}

	static base64toBlob(base64Data: any, contentType: any): Blob {
		try {
			const base64ImageContent = base64Data.replace(
				/^data:image\/(png|jpg|jpeg|webp);base64,/,
				''
			);
			contentType = contentType || '';
			const sliceSize = 1024;
			const byteCharacters = atob(base64ImageContent);
			const bytesLength = byteCharacters.length;
			const slicesCount = Math.ceil(bytesLength / sliceSize);
			const byteArrays = new Array(slicesCount);

			for (let sliceIndex = 0; sliceIndex < slicesCount; ++sliceIndex) {
				const begin = sliceIndex * sliceSize;
				const end = Math.min(begin + sliceSize, bytesLength);

				const bytes = new Array(end - begin);
				for (let offset = begin, i = 0; offset < end; ++i, ++offset) {
					bytes[i] = byteCharacters[offset].charCodeAt(0);
				}
				byteArrays[sliceIndex] = new Uint8Array(bytes);
			}
			const imageBlob = new Blob(byteArrays, { type: contentType });
			return imageBlob;
		} catch (e) {
			return new Blob();
		}
	}

	// get previous url from local storage
	static getPreviousPath(): string | undefined {
		if (localStorage.getItem('previous_path')) {
			let _url = localStorage.getItem('previous_path');
			return _url?.startsWith('/') ? _url?.substring(1) : _url || '';
		}
		return undefined;
	}
	// set previous url in local storage
	static setPreviousPath(url: string) {
		let _url = url?.startsWith('/') ? url?.substring(1) : url;
		localStorage.setItem('previous_path', _url);
	}
	// fetches the pathname without locale at the start
	static getPathnameWithoutLocale(
		pathname: string | undefined,
		locale: string
	): string | undefined {
		if (pathname?.startsWith(`/${locale}`)) {
			return pathname?.substring(4);
		}
		return pathname;
	}

	// patch function to reform correct custom logo url. (patch for the fundraiser having 'dpr_auto%2Cf_auto%2Cq_auto' in custom logo url)
	static getCloudinaryCustomLogoPath(url: string) {
		let _url = this.removeWebpFromUrl(url);
		if (_url?.search('q_auto/') > 0) {
			return (
				environment.cloudinaryBaseUrl +
				'dpr_auto,f_auto,q_auto/' +
				_url?.split('q_auto/')?.[1]
			);
		}
		if (_url?.search('upload/v1/') > 0) {
			return (
				environment.cloudinaryBaseUrl +
				'dpr_auto,f_auto,q_auto/' +
				_url?.split('upload/v1/')?.[1]
			);
		}
		return _url;
	}
	static removeWebpFromUrl(url: string) {
		return url.replace('.webp', '');
	}
}
